package EstructuraFor;

public class Ej1Estructurafor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Realizar un programa que imprima en pantalla los números del 1 al 100.
		 * */
		//creamos la variable f
        int f;
        //Bucle que se repite 100 veces y muestra los numeros del 1 al 100 separados por un guion.
        for(f=1;f<=100;f++) {
            System.out.print(f);
            System.out.print("-");
        }
	}

}
