package EstructuraFor;

public class Ej8Estructurafor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * Desarrollar un programa que muestre la tabla de multiplicar del 5 (del 5 al
		 * 50)
		 */
		// bucle que se repite 10 veces
		for (int f = 1; f <= 10; f++) {
			// Mostramos el resultado de multiplicar 5 por f, que va incrementando cada vez
			System.out.println("5 X " + f + " = " + (5 * f));
		}
	}

}
